'use strict';

import debug from 'debug';
import _ from 'lodash';

import Article from './Article';

const _debug = debug('moshi:classes:Report');
const reportNumber = Symbol.for('reportNumber');


/**
 * A Report generated by governmental or non-governmental organization.
 */
class Report extends Article {

  constructor(properties) {
    super(properties);
    let props = properties || {};
    _debug('props', props);
    _.extend(this, props);
  }

  get type() {
    return 'Report';
  }

  set reportNumber(value) {
    let sanitized = this.constructor.sanitizeValue(value, 'Text');
    this[reportNumber] = this.constructor.toArray(sanitized);
  }

  get reportNumber() {
    let value = this[reportNumber];
    return this.constructor.fromArray(value);
  }

}

export default Report;
